# Defining sets of white pieces.       
def isWhitePawn(piece):
    return (piece == pawnWhite or piece == pawnWhite2 or piece == pawnWhite3 or
            piece == pawnWhite4 or piece == pawnWhite5 or piece == pawnWhite6 or
            piece == pawnWhite7 or piece == pawnWhite8)

def isWhiteRook(piece):
    return (piece == rookWhite or piece == rookWhite2)
    
def isWhiteKnight(piece):
    return (piece == knightWhite or piece == knightWhite2)
    
def isWhiteBishop(piece):
    return (piece == bishopWhite or piece == bishopWhite2)
    
def isWhiteQueen(piece):
    return (piece == queenWhiteA or piece == queenWhite2 or piece == queenWhite3
            or piece == queenWhite4 or piece == queenWhite5 or piece == 
            queenWhite6 or piece == queenWhite7 or piece == queenWhite8 or 
            piece == queenWhite9)
            
def isWhiteKing(piece):
    return (piece == kingWhiteA)
            
def isWhite(piece):
    return (isWhitePawn(piece) == True or isWhiteRook(piece) == True or 
            isWhiteKnight(piece) == True or isWhiteBishop(piece) == True or
            isWhiteQueen(piece) == True or isWhiteKing(piece) == True)
            
